package com.leetcode.oj;

import static org.junit.Assert.assertEquals;

import org.junit.Test;

public class LongestPalindromicSubstringTest {
	LongestPalindromicSubstring lps = new LongestPalindromicSubstring();
	
	@Test
	public void testLongestPalindrome1(){
		String s = "090";
		assertEquals("090", longestPalindrome(s));
	}
	
	@Test
	public void testLongestPalindrome2(){
		String s = "abbaadsf";
		assertEquals("abba", longestPalindrome(s));
	}
	
	@Test
	public void testLongestPalindrome3(){
		String s = ",123454321.";
		assertEquals("123454321", longestPalindrome(s));
	}
	
	@Test
	public void testLongestPalindrome4(){
		String s = "asdfamanaplanacanalpanamaasdf12345654321asdf";
		assertEquals("amanaplanacanalpanama", longestPalindrome(s));
	}
	
	@Test
	public void testLongestPalindrome5(){
		String s = "";
		assertEquals("", longestPalindrome(s));
	}
	
	@Test
	public void testLongestPalindrome6(){
		String s = "a";
		assertEquals("a", longestPalindrome(s));
	}
	
	protected String longestPalindrome(String s) {
		return lps.longestPalindrome(s);
	}
}
